import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const DistantConfig = () => {
  const [ip, setIp] = useState("");
  const [login, setLogin] = useState("");
  const [password, setPassword] = useState("");
  const [hypervisor, setHypervisor] = useState("VirtualBox");
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (!ip || !login || !password) {
      alert("Veuillez remplir tous les champs !");
      return;
    }
    // On navigue vers le formulaire en passant les infos de connexion distante
    navigate("/formulaire", { state: { remote_ip: ip, remote_user: login, remote_password: password, hypervisor } });
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Configuration distante</h1>

      <label style={styles.label}>IP Address :</label>
      <input
        type="text"
        placeholder="Veuillez saisir l'IP de la machine distante..."
        value={ip}
        onChange={(e) => setIp(e.target.value)}
        style={styles.input}
      />

      <label style={styles.label}>Login :</label>
      <input
        type="text"
        placeholder="Veuillez saisir le login..."
        value={login}
        onChange={(e) => setLogin(e.target.value)}
        style={styles.input}
      />

      <label style={styles.label}>Password :</label>
      <input
        type="password"
        placeholder="Veuillez saisir le mot de passe..."
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={styles.input}
      />

      <label style={styles.label}>Hypervisor</label>
      <select
        value={hypervisor}
        onChange={(e) => setHypervisor(e.target.value)}
        style={styles.select}
      >
        <option value="VirtualBox">VirtualBox</option>
        <option value="VMware">VMware Workstation Pro</option>
      </select>

      <button style={styles.button} onClick={handleSubmit}>
        Connecter
      </button>
    </div>
  );
};

// ðŸŽ¨ Styles (identiques Ã  votre version)
const styles = {
  container: {
    textAlign: "center",
    padding: "50px",
    backgroundColor: "#E3FDFD",
    height: "100vh",
  },
  title: {
    fontSize: "50px",
    fontWeight: "bold",
    color: "#008080",
  },
  label: {
    display: "block",
    fontSize: "18px",
    fontWeight: "bold",
    marginTop: "20px",
    color: "#004D40",
  },
  input: {
    width: "80%",
    padding: "10px",
    marginTop: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  select: {
    width: "83%",
    padding: "10px",
    marginTop: "10px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  button: {
    marginTop: "20px",
    padding: "10px 20px",
    backgroundColor: "#008080",
    color: "white",
    fontSize: "18px",
    border: "none",
    cursor: "pointer",
    borderRadius: "5px",
  },
};

export default DistantConfig;
