import React, { useState } from 'react';

const CustomBox = ({ onClose, onAddBox }) => {
    const [boxName, setBoxName] = useState("");
    const [isoFile, setIsoFile] = useState(null);
    const [isoUrl, setIsoUrl] = useState("");
    const [username, setUsername] = useState(""); // Utilisateur SSH pour l'installation
    const [password, setPassword] = useState(""); // Mot de passe SSH
    const [newUser, setNewUser] = useState(""); // Nouvel utilisateur à créer sur la box
    const [newUserPassword, setNewUserPassword] = useState(""); // Mot de passe du nouvel utilisateur
    const [description, setDescription] = useState("");
    const [software, setSoftware] = useState({
        ssh: false,
        hadoop: false,
        python: false,
        nettools: false,
        java: false,
        ansible: false,
    });

    const handleAddBox = () => {
        if (boxName.trim() && (isoFile || isoUrl) && username && password) {
            const formData = new FormData();
            formData.append('name', boxName);
            formData.append('username', username);
            formData.append('password', password);
            formData.append('new_user', newUser);
            formData.append('new_user_password', newUserPassword);
            formData.append('description', description);
            formData.append('software', JSON.stringify(software));

            // Gestion ISO : fichier uploadé ou URL
            if (isoFile) {
                formData.append('isoFile', isoFile);
            } else {
                formData.append('isoUrl', isoUrl);
            }

            fetch('/api/create_box', {
                method: 'POST',
                body: formData
            })
                .then(response => response.json())
                .then(data => {
                    alert(data.message || "Box créée avec succès !");
                    onAddBox(data);
                    onClose();
                })
                .catch(err => {
                    console.error(err);
                    alert("Erreur lors de la création de la box.");
                });
        } else {
            alert("Les champs marqués * sont requis.");
        }
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setIsoFile(file || null);
        if (file) setIsoUrl(""); // Réinitialiser l'URL si un fichier est sélectionné
    };

    const handleUrlChange = (e) => {
        setIsoUrl(e.target.value);
        if (e.target.value) setIsoFile(null); // Réinitialiser le fichier si une URL est saisie
    };

    const handleSoftwareChange = (softwareName) => {
        setSoftware(prev => ({
            ...prev,
            [softwareName]: !prev[softwareName],
        }));
    };

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h2 className="text-lg font-bold mb-4">Créer votre nouvelle box</h2>

                <label className="block text-sm font-medium">Nom de la box * :</label>
                <input
                    placeholder="Entrez le nom de la box..."
                    type="text"
                    value={boxName}
                    onChange={(e) => setBoxName(e.target.value)}
                    className="w-full p-2 border rounded mb-4"
                />

                <label className="block text-sm font-medium mb-2">ISO image * :</label>
                <input
                    type="file"
                    onChange={handleFileChange}
                    className="w-full mb-2"
                    accept=".iso"
                />
                <div className="text-center mb-2 text-sm text-gray-500">OU</div>
                <input
                    type="url"
                    value={isoUrl}
                    placeholder="Saisissez l'URL d'un ISO..."
                    onChange={handleUrlChange}
                    className="w-full p-2 border rounded mb-4"
                />
                {isoFile && (
                    <p className="text-gray-600 text-sm mb-4">
                        Fichier sélectionné : {isoFile.name}
                    </p>
                )}

                <div className="flex space-x-4 mb-4">
                    <div className="flex-1">
                        <label className="block text-sm font-medium mb-2">SSH User *:</label>
                        <input
                            placeholder="Nom d'utilisateur SSH..."
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>
                    <div className="flex-1">
                        <label className="block text-sm font-medium mb-2">SSH Password *:</label>
                        <input
                            type="password"
                            placeholder="Mot de passe SSH..."
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                            required
                        />
                    </div>
                </div>

                <div className="flex space-x-4 mb-4">
                    <div className="flex-1">
                        <label className="block text-sm font-medium mb-2">Nouveau System User :</label>
                        <input
                            placeholder="Nom du nouvel utilisateur..."
                            type="text"
                            value={newUser}
                            onChange={(e) => setNewUser(e.target.value)}
                            className="w-full p-2 border rounded"
                        />
                    </div>
                    <div className="flex-1">
                        <label className="block text-sm font-medium mb-2">Mot de passe du nouvel utilisateur :</label>
                        <input
                            type="password"
                            placeholder="Mot de passe du nouvel utilisateur..."
                            value={newUserPassword}
                            onChange={(e) => setNewUserPassword(e.target.value)}
                            className="w-full p-2 border rounded"
                        />
                    </div>
                </div>

                <label className="block text-sm font-medium mb-2">Description :</label>
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Décrivez votre box..."
                    className="w-full p-2 border rounded mb-4"
                    rows="3"
                />

                <h3 className="text-sm font-medium mb-2">Logiciels :</h3>
                {Object.keys(software).map((key) => (
                    <label key={key} className="inline-flex items-center mb-2">
                        <input
                            type="checkbox"
                            checked={software[key]}
                            onChange={() => handleSoftwareChange(key)}
                            className="form-checkbox h-5 w-5 text-teal-600"
                        />
                        <span className="ml-2 text-gray-700 capitalize">{key}</span>
                    </label>
                ))}

                <button
                    onClick={handleAddBox}
                    className="w-full bg-teal-500 text-white p-2 rounded-lg shadow-md hover:bg-teal-600"
                >
                    Submit
                </button>
                <button
                    onClick={onClose}
                    className="mt-2 w-full bg-gray-300 text-black p-2 rounded-lg hover:bg-gray-400"
                >
                    Decline
                </button>
            </div>
        </div>
    );
};

export default CustomBox;
